shop_name=#{shopName, jdbcType=VARCHAR}
,shop_id=#{shopId, jdbcType=VARCHAR}
,phone_number=#{phoneNumber, jdbcType=VARCHAR}
where id = #{id, jdbcType=INTEGER}

update customer set
credit_value=#{creditValue, jdbcType=INTEGER}
where id = #{id, jdbcType=INTEGER}
